package com.goldy1992.leetcode._76_minimum_window_substring

import com.goldy1992.leetcode.TestUtils
import com.goldy1992.leetcode.TestUtils.buildFilePath
import com.goldy1992.leetcode.TestUtils.readFileAsString
import com.goldy1992.leetcode._30_substring_with_concatentation_of_all_words.SolutionTest
import org.junit.jupiter.api.Assertions.*
import kotlin.test.Test

class SolutionTest {
    private val solution = Solution()
    companion object {
        const val TEST_CLASS_RES_DIR = "src/test/resources/_76_minimum_window_substring"
    }

    @Test
    fun testExample1() {
        val s = "ADOBECODEBANC"
        val t = "ABC"
        val expected = "BANC"
        val result = solution.minWindow(s, t)
        assertEquals(expected, result)
    }
    @Test
    fun testExample2() {
        val s = "a"
        val t = "a"
        val expected = "a"
        val result = solution.minWindow(s, t)
        assertEquals(expected, result)
    }
    @Test
    fun testExample3() {
        val s = "a"
        val t = "aa"
        val expected = ""
        val result = solution.minWindow(s, t)
        assertEquals(expected, result)
    }

    @Test
    fun testExample4() {
        val s = "bdab"
        val t = "ab"
        val expected = "ab"
        val result = solution.minWindow(s, t)
        assertEquals(expected, result)
    }

    @Test
    fun testExample5() {
        val s = "abc"
        val t = "ab"
        val expected = "ab"
        val result = solution.minWindow(s, t)
        assertEquals(expected, result)
    }
    @Test
    fun testExample6() {
        val s = "bba"
        val t = "ab"
        val expected = "ba"
        val result = solution.minWindow(s, t)
        assertEquals(expected, result)
    }

    @Test
    fun testExample7() {
        val s = readFileAsString(buildFilePath(TEST_CLASS_RES_DIR, "test_7_s.txt"))
        val t = readFileAsString(buildFilePath(TEST_CLASS_RES_DIR, "test_7_t.txt"))
        val expected = ""
        val result = solution.minWindow(s, t)
        assertEquals(expected, result)
    }
}